import sys
sys.set_int_max_str_digits(214748364)
input = sys.stdin.readline


n=int(input())
res=1
file=open(f"C:/Users/kjmwp/Desktop/{n}!결과.txt",'w')
for i in range(n+1):
    res*=i+1
    data="%d \n %d \n "% (res,i) 
    file.write(data)
    print(res,end="\n")
res=str(res)
file.close()
print(len(res))
